pragma solidity ^0.4.0;
//pragma experimental ABIEncoderV2;

contract Message {
    address owner;

    modifier equalOwner() {
        if (msg.sender == owner) _;
    }

    modifier equalAuthor(address queryAddress){
        if (msg.sender == queryAddress) _;
    }

    struct Article {
        bytes32 articleID;
        string title;
        string body;
    }

    mapping(address => Article[]) Author2Article;
    //    mapping(string => address) NickName2Author;
    mapping(address => string) Author2NickName;

    constructor() public{
        owner = msg.sender;
    }

    function setNickName(string nick_name) public {
        Author2NickName[msg.sender] = nick_name;
    }

    function getNickName(address inputAddress) public view returns (string){
        return Author2NickName[inputAddress];
    }

    function publish(string title, string body) public returns (string){
        Article memory temp;
        temp.articleID = sha256(abi.encodePacked(msg.sender, title, body));
        temp.title = title;
        temp.body = body;

        Author2Article[msg.sender].push(temp);
        return temp.title;
    }

